    Pagination_Affect() {
      const NoOfArrays = Math.ceil(this.TotalRows / this.LimitCount);
      const PrevClass = this.CurrentIndex > 1 ? '' : 'PageAction_Disabled';
      this.PagePrevious = {
        Disabled: !(this.CurrentIndex > 1),
