        Value: this.CurrentIndex + 1,
        Class: NextClass
      };
      this.PagesArray = [];

      for (let index = 1; index <= NoOfArrays; index++) {
        if (index === 1) {
          this.PagesArray.push({
            Text: '1',
            Class: 'Number',
            Value: 1,
            Show: true,
            Active: this.CurrentIndex === index
          });
        }

        if (index > 1 && NoOfArrays > 2 && index < NoOfArrays) {
          if (index === this.CurrentIndex - 2) {
            this.PagesArray.push({
              Text: '...',
              Class: 'Dots',
              Show: true,
              Active: false
            });
          }

          if (index === this.CurrentIndex - 1) {
            this.PagesArray.push({
              Text: (this.CurrentIndex - 1).toString(),
              Class: 'Number',
              Value: index,
              Show: true,
              Active: false
            });
          }

          if (index === this.CurrentIndex) {
            this.PagesArray.push({
              Text: this.CurrentIndex.toString(),
              Class: 'Number',
              Value: index,
              Show: true,
              Active: true
            });
          }

          if (index === this.CurrentIndex + 1) {
            this.PagesArray.push({
