          if (index === this.CurrentIndex + 2) {
            this.PagesArray.push({
              Text: '...',
              Class: 'Dots',
              Show: true,
              Active: false
            });
          }
        }

        if (index === NoOfArrays && NoOfArrays > 1) {
          this.PagesArray.push({
            Text: NoOfArrays.toString(),
            Class: 'Number',
            Value: NoOfArrays,
            Show: true,
            Active: this.CurrentIndex === index
          });
        }
      }

      let ToCount = this.SkipCount + this.LimitCount;

      if (ToCount > this.TotalRows) {
        ToCount = this.TotalRows;
